import React, {useState} from 'react'
import { useContext } from 'react';
import {ItunesApiContext} from './ItunesApiContext'

const Search = () => {
    const [keyword, setKeyword] = useState('');

    //API contextからgetSearchedMusic関数を取ってくる
    const {getSearchedMusic} = useContext(ItunesApiContext);

    const onSearch = () => {
        console.log(getSearchedMusic(keyword))
    }

  return (
    <div>
        <input 
            type="text"
            className="search-input"
            value={keyword}
            onChange={(e)=>setKeyword(e.target.value)}
        />
        <button className="search-botton" onClick={onSearch(keyword)}>検索</button>
    </div>
  )
}

export default Search
