import qs from "qs";
import axios from "axios";
import { createContext } from "react";
export const ItunesApiContext = createContext();

const ItunesApiContextProvider = (props) => {
    const apiClient = axios.create({
        baseURL: "https//itunes.apple.com"
    });

    const [searchResult, setSearchResult] = useState([]);

    const getSearchedMusic = async (term) => {
        //検索するパラメーターを設定
        const params = {
            term,
            media: "music",
            entity: "musicTrack",
            country: "jp",
            lang: "ja_jp",
            limit: "50"
        };
        // baseURLに設定したparamsを使ってgetリクエストを送信
        setSearchResult(apiClient.get(`/search?${qs.stringify(params)}`));
    }

    return (
        <ItunesApiContext.Provider
        value={{
            getSearchedMusic,
        }}
        >
            {props.children}
        </ItunesApiContext.Provider>
    )
}

export default ItunesApiContextProvider
