import React from 'react'
import {useState} from "react";
import './App.css';
import {Link} from "@mui/material"

function Login2() {

  const initialValues = {id:"",password:""};//初期値
  const [formValues, setFormValues] = useState(initialValues); 
  const [formErrors, setFormErrors] = useState({}); //{}←オブジェクト　キーと値を入れる為irerutame
  const [isSubmit,setIsSubmit]= useState(false);

  const handleChange = (e) =>{
    //console.log(e.target.value);
    const {name,value} = e.target;//分割代入　　
    setFormValues({...formValues,[name]:value});
    //console.log(formValues);
  };

  const handleSubmit = (e) => {
    e.preventDefault();//更新を妨げる
    //ログイン情報を送信する
    //バリデーションチェックをする
    setFormErrors(validate(formValues));
    setIsSubmit(true);
  };

  const validate = (values) => {
    //
    console.log(values)
    const errors = {};//どの欄でエラーが吐かれているのかパターンを用意しておく

    if(!values.id){
      errors.id ="ユーザーIDを入力してください";
    }



    if(!values.password){
      errors.password ="パスワードを入力してください";
    }
    else if(values.password.length < 4){
      errors.password = "4文字以上15文字以下のパスワードを入力してください";
    }
    else if(values.password.length > 15){
      errors.password = "4文字以上15文字以下のパスワードを入力してください";
    }
    return errors;
  };

  return (
 <div className="formContainer">
      <form onSubmit={(e) => handleSubmit(e)}>{/* onSubmitプロパティは*/}
      <h1>ログインフォーム</h1>
        <hr />{/*横線 */}
        <div className="uiForm">

            <div className="formField">
              <label>ユーザーID</label>
              <input type = "text" 
              placeholder="ユーザーID"
               name="id" 
              onChange={(e)=> handleChange(e)} //で文字が打ち込まれてる時(onChange)にhandleChangeを呼び出す
              />
              </div>
              <p className='errorMsg'>{formErrors.id}</p>


              <div className="formField">
                <label>パスワード</label>
                <input type = "text" 
                placeholder="パスワード" 
                name="password"
                onChange={(e)=> handleChange(e)} 
                 />
              </div>
              <p className='errorMsg'>{formErrors.password}</p>

              <button className = "submitButton">ログイン</button>
              <div className='new_account'>
                <button className='newAccountButton'>アカウント新規作成</button>
                </div>

              {Object.keys(formErrors).length === 0 && isSubmit && (
                <div className='msgOK'>ログインに成功しました</div>
              )}
           </div>
      </form>
    </div>

  )
}

export default Login2;
